    private void punetazoKnightMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_punetazoKnightMousePressed
        // TODO add your handling code here:
        double random = Math.random() * 90;
        int nueva_vida = vidaPollo.getValue() - (int) random;
        vidaPollo.setValue(nueva_vida);
        if (nueva_vida <= 0) {
            jugando.setText("GANASTE");
            return;
        }

        Timer timer = new Timer(1000, new AbstractAction() {
            @Override
            public void actionPerformed(ActionEvent ae) {
                double ataque_random = Math.random() * 100;
                if (vidaPollo.getValue() == 0) {
                    System.out.print("Dead pollo");
                    return;
                }

                if (ataque_random < 40) {
                    System.out.println("ATAQUE: NORMAL");
                    double random = Math.random() * 30;
                    int nueva_vida = vidaKnight.getValue() - (int) random;
                    vidaKnight.setValue(nueva_vida);
                    if (nueva_vida <= 0) {
                        jugando.setText("GANO EL KNIGHT");
                    }
                } else if (ataque_random < 80) {
                    System.out.println("SE CURA");
                    double random = Math.random() * 25;
                    int nueva_vida = vidaPollo.getValue() + (int) random;
                    vidaPollo.setValue(nueva_vida);
                    if (nueva_vida <= 0) {
                        jugando.setText("GANO EL POLLO MUTANTE");
                    }
                } else if (ataque_random < 90) {
                    System.out.println("ATAQUE: 2");
                    double random = Math.random() * 90;
                    int nueva_vida = vidaPollo.getValue() - (int) random;
                    vidaKnight.setValue(nueva_vida);
                    if (nueva_vida <= 0) {
                        jugando.setText("GANO EL POLLO MUTANTE");
                    }
                }
            }

        });
        timer.setRepeats(false);
        timer.start();
    }//GEN-LAST:event_punetazoKnightMousePressed

